import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Form, FormControl, Button } from 'react-bootstrap';
import data from "./data.json";
import React from "react";
import Products from './components/Products';
import Filter from './components/Filter';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      products: data.products,
      era: "",
      merch: "",
      sort: "",
    }
  }
  sortProducts = (event) => {
    //impl
    const sort = event.target.value
    this.setState((state) => ({
      sort: sort,
      products: this.state.products.slice().sort((a, b) => (
        sort === "lowest" ?
          ((a.price < b.price) ? 1 : -1) :
          sort === "highest" ?
            ((a.price > b.price) ? 1 : -1) :
            ((a._id > b._id) ? 1 : -1)

      ))
    }))

  }
  filterEra(event) {
    //impl
    console.log(event.target.value)
    // if (event.target.value === ""){
    //   this.setState({era: event.target.value, product: data.products});
    // } else {
    //   this.setState({
    //     era: event.target.value,
    //     products: data.products.filter(product => product.era.includes(event.target.value))
    //   })
    // }
  }
  filterMerch(event) {
    //impl
    console.log(event.target.value)
  }
  render() {
    return (
      <div className="ariana grande shop">
        <Navbar bg="dark" variant="dark">
          <Navbar.Brand href="#home">
            <img
              alt=""
              src="white-heart.png"
              width="30"
              height="30"
              className="d-inline-block align-top"
            />{' '}
            Ariana Grande Shop
          </Navbar.Brand>
        </Navbar>
        <main>
          <div className="wrapper" >
            <div className="cards-container">
              <Filter count={this.state.products.length}
                era={this.state.era}
                merch={this.state.merch}
                sort={this.state.sort}
                filterEras={this.filterEras}
                filterMerch={this.filterMerch}
                sortProducts={this.sortProducts}></Filter>
              <Products products={this.state.products}></Products>
            </div>
            <div className="cart-container"><button>heeey</button></div>
          </div>
        </main>
      </div>
    );
  }
}

export default App;
